///给所有prismjs添加行内行号
function addLineNumbers() {
  var x = document.getElementsByTagName("pre");
  if (x==null) {
    return;
  }
  for (var i = 0; i < x.length; i++) {
    x[i].classList.add("line-numbers");
  }
}
